-- Contenu de la table `motifsignals`
--

INSERT INTO `motifsignals` (`id`, `name`, `slug`, `description`, `parent_group`, `created_at`, `updated_at`, `importance`) VALUES
(1, 'Contenus illicites', 'contenus-illicites', 'Concerne tous les contenus punis par la loi.', 0, '2017-05-03 07:03:55', '2017-05-04 04:17:25', 3),
(2, 'Contenus interdits', 'contenus-interdits', 'Concerne tous les contenus interdits sur le site.', 0, '2017-05-03 07:04:18', '2017-05-04 04:17:59', 2),
(4, 'Contenus à modérer', 'contenus-a-moderer', 'Tous les contenus ne respectant pas la charte du site.', 0, '2017-05-03 07:05:56', '2017-05-03 07:05:56', 1),
(5, 'Pédopornographie', 'pedopornographie', 'Concerne la diffusion de contenus à caractère pédopornographiques, quel que soit le format. Concerne également la diffusion d\'images pouvant choquer les mineurs. La pornographie n\'entre pas dans cette catégorie.', 1, '2017-05-03 07:06:28', '2017-05-03 07:06:28', 0),
(6, 'Incitation à la haine, discrimination', 'incitation-a-la-haine-discrimination', 'Concerne les propos discriminatoires ou incitant à la haine contre les personnes en raison de leurs origines, leur religion, leur orientation sexuelle, etc. Les insultes n\'entrent pas dans cette catégorie.', 1, '2017-05-03 07:06:49', '2017-05-03 07:06:49', 0),
(7, 'Mise en danger des personnes', 'mise-en-danger-des-personnes', 'Concerne les propos considérés comme dangereux pour la vie des personnes. Entrent dans cette catégorie tous propos de personnes menaçant de commettre un meurtre ou une attaque dans la vie réelle. Les menaces physiques sur un individu particulier n\'entrent pas dans cette catégorie.\r\n', 1, '2017-05-03 07:07:11', '2017-05-03 07:07:11', 0),
(8, 'Diffamation, menaces', 'diffamation-menaces', 'Concerne des propos portant atteinte à l\'honneur d\'une personne physique ou morale. Les insultes n\'entrent pas dans cette catégorie. Les propos menaçants sont des propos mettant en danger les personnes par des menaces de blessures physiques, ou par la volonté claire d\'une personne de retrouver une autre personne dans la vie réelle. Les insultes n\'entrent pas dans cette catégorie.', 1, '2017-05-03 07:07:38', '2017-05-03 07:07:38', 0),
(9, 'Piratage, non respect des droits d\'auteur', 'piratage-non-respect-des-droits-d-auteur', 'Concerne la demande ou la diffusion de contenus protégés par le droit d\'auteur. Concerne également les demandes d\'aide au piratage de comptes, ou les messages contenant des logiciels malveillants. L\'attaque de sites extérieurs et les propos diffamatoires n\'entrent pas dans cette catégorie.', 1, '2017-05-03 07:07:59', '2017-05-03 07:07:59', 0),
(10, 'Apologie de comportements illégaux', 'apologie-de-comportements-illegaux', 'Concerne tout propos pouvant être punis par la loi. Négationnisme, consommation de drogues, incitation à commettre des infractions.', 1, '2017-05-03 07:08:34', '2017-05-03 07:08:34', 0),
(11, 'Données personnelles', 'donnees-personnelles', 'Concerne la diffusion d\'informations personnelles et confidentielles sur un utilisateur, qu\'elles soient déjà accessibles sur Internet ou non. Les informations confidentielles peuvent être : un nom, un prénom, une adresse, un e-mail, une adresse IP, une photo ou une vidéo permettant de reconnaitre la personne en question, etc.', 1, '2017-05-03 07:08:52', '2017-05-03 07:08:52', 0),
(12, 'Pornographie', 'pornographie', 'Concerne la diffusion de contenus dont le visionnage est interdit aux mineurs. La pédopornographie n\'entre pas dans cette catégorie.', 2, '2017-05-03 07:09:35', '2017-05-03 07:09:35', 0),
(13, 'Insultes', 'insultes', 'Concerne les propos insultants et blessants dont le but est clairement de porter tort à la personne visée. Les propos diffamatoires, les menaces et les propos discriminatoires n\'entrent pas dans cette catégorie.', 2, '2017-05-03 07:09:50', '2017-05-03 07:09:50', 0),
(14, 'Flood de masse', 'flood-de-masse', 'Concerne les messages incohérents avec le sujet et postés massivement dans le but avéré de nuire à la lisibilité du site. Les messages avec un seul mot ou smiley et les chaines de messages n\'entrent pas dans cette catégorie.', 2, '2017-05-03 07:10:17', '2017-05-03 07:10:17', 0),
(15, 'Raids, mass-dislikes, attaque de sites', 'raids-mass-dislikes-attaque-de-sites', 'Concerne tous les messages dont le but est d\'organiser une attaque sur un site extérieur à Le coin des Écrivains. Le Mass-dislike est l\'action qui consiste à demander aux membres de baisser massivement et artificiellement la note d\'une contenu sur un site pour le polluer. Les raids sont des regroupements de membres dont le but est de se retrouver sur un site pour le polluer.\r\n', 2, '2017-05-03 07:12:31', '2017-05-03 07:12:31', 0),
(16, 'Spoilers', 'spoilers', 'Concerne les messages dont le but volontaire est de gâcher une expérience de jeu, un film, ou quelque média que ce soit, en dévoilant des éléments importants de l\'intrigue.', 2, '2017-05-03 07:14:28', '2017-05-03 07:14:28', 0),
(17, 'Message inoportun', 'message-inoportun', 'Concerne les messages parasitant une discussion. Cela peut être un message avec un unique mot ou smiley sans rapport avec la discussion, un message copiant parfaitement le message précédent dans le but de créer une chaîne. Le flood de masse n\'entre pas dans cette catégorie.', 4, '2017-05-03 07:15:10', '2017-05-03 07:15:10', 0),
(18, 'Doublons', 'doublons', 'Concerne les topics dont le sujet a déjà été traité sur un autre topic déjà existant. Attention : pour que l\'alerte soit validée, merci de mettre un lien vers le topic déjà existant dans le champ "Remarques".', 4, '2017-05-03 07:16:17', '2017-05-03 07:16:17', 0),
(19, 'Publicité', 'publicite', 'Concerne les messages de publicité intempestive, hors-sujet au site ou au texte concerné et dont le but est exclusivement de faire de la publicité.\r\n', 4, '2017-05-03 07:16:47', '2017-05-03 07:16:47', 0);

