INSERT INTO `users` (`id`, `name`, `email`, `password`, `avatar`, `birthday`, `country`, `sex`, `description`, `role_id`, `confirmed`, `confirmation_token`, `remember_token`, `created_at`, `updated_at`, `karma`) VALUES
(1, 'UnBaladinEnMaraude', 'loic.holleville@viacesi.fr', '$2y$10$7T0oTOH.xod1YhRwPDAta.oemKJtfgq5cnvjyiGlXrI8imaQoUFDy', 1, '1996-02-13 11:40:34', 'France', '1', '<p>Auteur passion&eacute; vivant &agrave;&nbsp;<strong>Rouen</strong>, je suis &eacute;galement le cr&eacute;ateur de ce site, qui est une &eacute;tude pour mon projet de maison d&#39;&eacute;dition.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Je fais des &eacute;tudes d&#39;<strong>ing&eacute;nieur en informatique</strong>, j&#39;en suis &agrave; ma troisi&egrave;me ann&eacute;e. Je suis tr&egrave;s attach&eacute; au&nbsp;<strong>Danemark</strong>, o&ugrave; je suis d&eacute;j&agrave; all&eacute; vivre 2 mois fin 2015, j&#39;y retourne pour travailler en laboratoire de recherche 6 mois &agrave; la rentr&eacute;e 2017-2018.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>J&#39;&eacute;cris beaucoup de&nbsp;<strong>fantasy</strong>, et j&#39;ai quelques petites nouvelles &eacute;crites dans le cadre du concours&nbsp;<u>Arr&ecirc;t sur Images</u>&nbsp;de&nbsp;<strong>JVC</strong>, le forum &eacute;criture. Il en d&eacute;coule un recueuil de textes qui sera bient&ocirc;t publi&eacute; en version papier.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Si jamais vous passez sur&nbsp;<strong>Rouen</strong>, pr&eacute;venez moi, on &eacute;clusera le gorgeon ensemble !</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>UnBaladinEnMaraude</em></p>', 2, 1, NULL, '29uDBD89cHk0j5GMkB9d3KUxNeK0bzWMTIiiSbrwtMFJecMZDhCSs4Pd2g3Q', '2018-03-05 13:40:07', '2018-04-05 09:57:20', 20),
(2, 'Chimène_Azalée', 'chimene@ilfingard.fr', '$2y$10$Boi50HTdE6YDn/xOzBjowungfnczNjUTBSSEykm3a7EjIOX7FQLQS', 1, '1010-01-11 15:02:42', 'Denmark (Danmark)', '2', NULL, 3, 1, '', 'kMKI1U5Nanctw7JzYAQgIux7qObCveZnAwRig6IbQEoZPQZ2dBgcuE6Z1dtv', '2018-03-07 07:58:43', '2018-04-17 13:02:42', 20),
(3, 'AdonisShibo', 'adonibis@gmail.com', '$2y$10$hggecqw2k/zrRQncVoAO6.1SYHIBuCZeVW9jGAqMFXI4YfYcVDT7q', 1, '1997-10-29 23:55:20', 'France', '1', 'Juste ici pour le fun, j\'écris pas de littérature. ', 1, 1, '', 'psUcB0wbGYXaiPNcGgYp5FTRc9oyLIqWUA50EEGMrD83MFNANnSJFD2TJWXo', '2018-03-07 08:00:30', '2018-04-18 13:42:53', 20),
(4, 'Scapin', 'scapin@ilfingard.fr', '$2y$10$uD1jE64W4ZZwCrH9LBlFsOnvtRF.NmAHc3kxwwUpj/Te4Gjg8deTW', 0, NULL, NULL, NULL, NULL, 1, 1, NULL, 'v4vmRVkLY2e8GCsrO208TUGs5cD6eJqj1lzlY4vr3CkhRatHpuGkc4gYauKk', '2018-03-07 08:03:13', '2018-03-07 08:03:37', 20);

